import { useEffect, useRef, type RefObject } from 'react';

export function useScrollToBottom(): [
  RefObject<HTMLDivElement>,
  RefObject<HTMLDivElement>,
] {
  const containerRef = useRef<HTMLDivElement>(null);
  const endRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const container = containerRef.current;
    const end = endRef.current;

    if (container && end) {
      const observer = new MutationObserver(() => {
        end.scrollIntoView({ behavior: 'instant', block: 'end' });
      });

      observer.observe(container, {
        childList: true,
        subtree: true,
        attributes: true,
        characterData: true,
      });

      return () => observer.disconnect();
    }
  }, []);

  // @ts-expect-error error
  return [containerRef, endRef];
}

lib/use-optimized-chat.ts.content:
"use client";

import { useState, useRef, FormEvent, ChangeEvent } from "react";
import { flushSync } from "react-dom";

type Message = {
  id: string;
  role: "user" | "assistant";
  content: string;
  parts?: any[];
};

type UseChatOptions = {
  api: string;
  body?: Record<string, any>;
  onError?: (error: Error) => void;
};

type ChatStatus = "ready" | "streaming" | "error";

export function useOptimizedChat(options: UseChatOptions) {
  const { api, body, onError } = options;

  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState("");
  const [status, setStatus] = useState<ChatStatus>("ready");
  const [, forceUpdate] = useState({});
  const abortControllerRef = useRef<AbortController | null>(null);
  const messagesRef = useRef<Message[]>([]);

  const handleInputChange = (e: ChangeEvent<HTMLInputElement> | ChangeEvent<HTMLTextAreaElement>) => {
    setInput(e.target.value);
  };

  const append = async ({ role, content }: { role: "user" | "assistant"; content: string }) => {
    const userMessage: Message = {
      id: `user-${Date.now()}-${Math.random()}`,
      role,
      content,
    };

    // flushSync: natychmiastowe wy≈õwietlenie wiadomo≈õci u≈ºytkownika
    flushSync(() => {
      setMessages((prev) => {
        const updated = [...prev, userMessage];
        messagesRef.current = updated;
        return updated;
      });
      setStatus("streaming");
    });

    try {
      abortControllerRef.current = new AbortController();
      const timestamp = Date.now();

      const response = await fetch(`${api}?_=${timestamp}`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Cache-Control": "no-cache, no-store, must-revalidate",
          "Pragma": "no-cache",
        },
        body: JSON.stringify({
          messages: messagesRef.current,
          timestamp,
          ...body,
        }),
        signal: abortControllerRef.current.signal,
        cache: "no-store",
      });

      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const reader = response.body?.getReader();
      if (!reader) throw new Error("No reader");

      const decoder = new TextDecoder();
      let buffer = "";
      let currentTextMessageId: string | null = null;

      // Real-time streaming - proces ka≈ºdƒÖ kompletnƒÖ liniƒô natychmiast
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        // Dekoduj natychmiast bez buforowania
        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        for (const line of lines) {
          if (!line.trim()) continue;

          try {
            const data = JSON.parse(line);
            console.log('[CLIENT PARSE]', data.type, Date.now());

            // TEXT DELTA - ABSOLUTNIE ZERO BATCHING
            if (data.type === "text-delta") {
              if (!currentTextMessageId) {
                const newMsg: Message = {
                  id: `text-${Date.now()}-${Math.random()}`,
                  role: "assistant",
                  content: data.delta,
                };
                currentTextMessageId = newMsg.id;

                // WYMUSZENIE synchronicznego update BEZ batching
                flushSync(() => {
                  messagesRef.current = [...messagesRef.current, newMsg];
                  setMessages([...messagesRef.current]);
                });
                // Dodatkowe wymuszenie rerenderu
                requestAnimationFrame(() => {
                  forceUpdate({});
                });
              } else {
                // NATYCHMIASTOWA aktualizacja - ZERO op√≥≈∫nie≈Ñ
                flushSync(() => {
                  messagesRef.current = messagesRef.current.map((msg) =>
                    msg.id === currentTextMessageId
                      ? { ...msg, content: msg.content + data.delta }
                      : msg
                  );
                  setMessages([...messagesRef.current]);
                });
                // Dodatkowe wymuszenie rerenderu
                requestAnimationFrame(() => {
                  forceUpdate({});
                });
              }
            }

            // TOOL CALL START - ZERO BATCHING
            else if (data.type === "tool-call-start") {
              currentTextMessageId = null;
              const toolMsg: Message = {
                id: `tool-${data.toolCallId}-${Date.now()}`,
                role: "assistant",
                content: "",
                parts: [{
                  type: "tool-invocation",
                  toolInvocation: {
                    toolCallId: data.toolCallId,
                    toolName: "",
                    args: {},
                    argsText: "",
                    state: "streaming",
                  },
                }],
              };

              flushSync(() => {
                messagesRef.current = [...messagesRef.current, toolMsg];
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL NAME - ZERO BATCHING
            else if (data.type === "tool-name-delta") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          toolName: data.toolName,
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL ARGUMENTS - ZERO BATCHING
            else if (data.type === "tool-argument-delta") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    const currentArgsText = msg.parts[0].toolInvocation.argsText || "";
                    const newArgsText = currentArgsText + data.delta;
                    let parsedArgs = msg.parts[0].toolInvocation.args;
                    try {
                      parsedArgs = JSON.parse(newArgsText);
                    } catch (e) {
                      // Zachowaj stare args dop√≥ki JSON nie jest kompletny
                    }
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          argsText: newArgsText,
                          args: parsedArgs,
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL INPUT AVAILABLE - ZERO BATCHING
            else if (data.type === "tool-input-available") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          args: data.input,
                          state: "call",
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL OUTPUT - ZERO BATCHING
            else if (data.type === "tool-output-available") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          state: "result",
                          result: data.output,
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }
          } catch (e) {
            if (!(e instanceof SyntaxError)) throw e;
          }
        }
      }

      flushSync(() => {
        setStatus("ready");
      });
    } catch (error) {
      if (error instanceof Error && error.name === "AbortError") {
        setStatus("ready");
        return;
      }

      setStatus("error");
      if (onError && error instanceof Error) {
        onError(error);
      }
      console.error("Chat error:", error);
    }
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim() || status === "streaming") return;

    const userInput = input;
    setInput("");
    await append({ role: "user", content: userInput });
  };

  const stop = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      abortControllerRef.current = null;
    }
    setStatus("ready");
  };

  return {
    messages,
    input,
    handleInputChange,
    handleSubmit,
    status,
    stop,
    append,
    setMessages: (msgs: Message[]) => {
      messagesRef.current = msgs;
      setMessages(msgs);
    },
  };
}

JSON-LINES-FORMAT.md.content:
# JSON Lines Format - Comet Gemini

## üìã Co to JSON Lines?

**JSON Lines** (`.jsonl`) to format tekstowy gdzie:
- Ka≈ºda linia = jeden kompletny JSON object
- Linie oddzielone znakiem `\n` (newline)
- **BEZ** prefix "data: " (to jest SSE format)
- Prosty do parsowania w streaming

## Format vs SSE

### ‚ùå SSE (Server-Sent Events)
```
data: {"type":"text-delta","delta":"Hello"}\n
\n
data: {"type":"text-delta","delta":" world"}\n
\n
```
- Wymaga prefix `data: `
- Podw√≥jny newline `\n\n` miƒôdzy eventami
- Bardziej z≈Ço≈ºony parsing

### ‚úÖ JSON Lines (u≈ºywamy w projekcie)
```
{"type":"text-delta","delta":"Hello","timestamp":1234567890}\n
{"type":"text-delta","delta":" world","timestamp":1234567891}\n
```
- Czysty JSON
- Pojedynczy newline `\n`
- Prostszy i szybszy parsing

## Implementacja w projekcie

### Serwer (app/api/chat/route.ts)

```typescript
const encoder = new TextEncoder();
const stream = new ReadableStream({
  async start(controller) {
    const sendEvent = (data: any) => {
      // Dodaj metadane
      const eventData = {
        ...data,
        timestamp: Date.now(),
        requestId: requestId || "unknown",
      };

      // JSON Lines: JSON + newline
      const line = JSON.stringify(eventData) + '\n';
      controller.enqueue(encoder.encode(line));

      console.log(`[STREAM] Sent: ${data.type}`);
    };

    // Wysy≈Çanie event√≥w
    sendEvent({ type: "text-delta", delta: "Hello" });
    sendEvent({ type: "text-delta", delta: " world" });
  }
});
```

### Klient (lib/use-optimized-chat.ts)

```typescript
const reader = response.body?.getReader();
const decoder = new TextDecoder();
let buffer = "";

while (true) {
  const { done, value } = await reader.read();
  if (done) break;

  // Dekoduj chunk
  buffer += decoder.decode(value, { stream: true });

  // Split na linie
  const lines = buffer.split('\n');
  buffer = lines.pop() || ''; // Zachowaj niekompletnƒÖ liniƒô

  // Parsuj ka≈ºdƒÖ kompletnƒÖ liniƒô
  for (const line of lines) {
    if (!line.trim()) continue; // Skip pustych linii

    try {
      // Czysty JSON parse - BEZ slice(6)!
      const data = JSON.parse(line);

      // Natychmiastowe renderowanie z flushSync
      if (data.type === "text-delta") {
        flushSync(() => {
          setMessages(prev => /* ... */);
        });
      }
    } catch (e) {
      if (!(e instanceof SyntaxError)) throw e;
    }
  }
}
```

## Typy event√≥w

### Text streaming
```json
{"type":"text-delta","delta":"tekst","timestamp":1234567890}
```

### Tool calls
```json
{"type":"tool-call-start","toolCallId":"call_123","timestamp":1234567890}
{"type":"tool-name-delta","toolCallId":"call_123","toolName":"computer","timestamp":1234567891}
{"type":"tool-argument-delta","toolCallId":"call_123","delta":"{\"action\":","timestamp":1234567892}
{"type":"tool-input-available","toolCallId":"call_123","input":{"action":"screenshot"},"timestamp":1234567893}
{"type":"tool-output-available","toolCallId":"call_123","output":"Done","timestamp":1234567894}
```

### Screenshot updates
```json
{"type":"screenshot-update","screenshot":"base64...","timestamp":1234567895}
```

### Completion
```json
{"type":"finish","timestamp":1234567896}
```

## Zalety JSON Lines w naszym projekcie

1. **Prostota** - brak SSE boilerplate
2. **Szybko≈õƒá** - mniej parsowania, bezpo≈õredni JSON.parse()
3. **Debugging** - ≈Çatwe czytanie w network tab
4. **Kompatybilno≈õƒá** - dzia≈Ça z ka≈ºdym fetch() API
5. **Real-time** - natychmiastowe przetwarzanie ka≈ºdej linii

## Kluczowe zasady

### ‚úÖ DO:
- U≈ºywaj `JSON.parse(line)` bezpo≈õrednio
- Split na `\n`
- Zachowuj niekompletne linie w bufferze
- U≈ºywaj `flushSync()` dla ka≈ºdej aktualizacji

### ‚ùå DON'T:
- ~~Nie u≈ºywaj `line.slice(6)`~~ (to dla SSE)
- ~~Nie sprawdzaj `line.startsWith('data: ')`~~ (to dla SSE)
- ~~Nie dziel na `\n\n`~~ (to dla SSE)
- Nie buforuj kompletnych linii przed parsowaniem

## Testowanie

### W DevTools Network:
1. Otw√≥rz request do `/api/chat`
2. W Response ‚Üí Preview powiniene≈õ widzieƒá:
```
{"type":"text-delta","delta":"H",...}
{"type":"text-delta","delta":"e",...}
{"type":"text-delta","delta":"l",...}
```

### W konsoli serwera:
```
[STREAM] Sent: text-delta at 2025-10-19T22:00:00.000Z
[STREAM] Sent: text-delta at 2025-10-19T22:00:00.001Z
```

## Migracja z SSE do JSON Lines

Je≈õli masz stary kod z SSE:

```typescript
// ‚ùå STARE (SSE)
if (line.startsWith('data: ')) {
  const data = JSON.parse(line.slice(6));
}

// ‚úÖ NOWE (JSON Lines)
if (line.trim()) {
  const data = JSON.parse(line);
}
```

## Referencje

- [JSON Lines spec](https://jsonlines.org/)
- MDN: [ReadableStream](https://developer.mozilla.org/en-US/docs/Web/API/ReadableStream)
- React: [flushSync](https://react.dev/reference/react-dom/flushSync)

